mapper.select:
SELECT ta.id, tu.nickname AS author, ta.user_id, ta.title, ta.summary, ta.content, ta.banner, ta.`status`, tat.type, ta.create_time FROM article AS ta LEFT JOIN `user` AS tu ON ta.user_id = tu.id LEFT JOIN `article_type` AS tat ON ta.type = tat.id WHERE ta.deleted = 0
AND tu.nickname LIKE CONCAT('%', #{params.author}, '%')
AND ta.title LIKE CONCAT('%', #{params.title}, '%')
AND ta.status = #{params.status}
AND ta.type = #{params.type}
AND ta.create_time BETWEEN #{startDate} AND #{endDate}
ORDER BY ta.create_time DESC
