mapper.select:
SELECT DATE_FORMAT(tor.create_time, '%Y-%m-%d') AS datetime, SUM(tor.fee) AS total_fee, SUM(IF(tor.paid = 1,tor.fee,0)) AS actual_received FROM tb_order AS tor LEFT JOIN application AS tap ON tap.id = tor.application_id LEFT JOIN `user` AS tu ON tu.id = tap.user_id WHERE tor.deleted = 0
AND tor.create_time BETWEEN #{startDate} AND #{endDate}
GROUP BY datetime ORDER BY datetime DESC

SELECT DATE_FORMAT(tor.create_time, '%Y年第%U周') AS datetime, SUM(tor.fee) AS total_fee, SUM(IF(tor.paid = 1,tor.fee,0)) AS actual_received FROM tb_order AS tor LEFT JOIN application AS tap ON tap.id = tor.application_id LEFT JOIN `user` AS tu ON tu.id = tap.user_id WHERE tor.deleted = 0
AND tor.create_time BETWEEN #{startDate} AND #{endDate}
GROUP BY datetime ORDER BY datetime DESC

SELECT DATE_FORMAT(tor.create_time, '%Y年%m月') AS datetime, SUM(tor.fee) AS total_fee, SUM(IF(tor.paid = 1,tor.fee,0)) AS actual_received FROM tb_order AS tor LEFT JOIN application AS tap ON tap.id = tor.application_id LEFT JOIN `user` AS tu ON tu.id = tap.user_id WHERE tor.deleted = 0
AND tor.create_time BETWEEN #{startDate} AND #{endDate}
GROUP BY datetime ORDER BY datetime DESC

